"use strict";function mainMenuOpen(){var e=document.querySelectorAll(".mainMenu"),n=document.querySelectorAll(".mainMenu__button");n.length>0&&n.forEach(function(n){n.addEventListener("click",function(n){this.classList.toggle("mainMenu__button_open"),e.forEach(function(e){e.classList.toggle("mainMenu_open")})})})}function productOpen(){var e=document.querySelectorAll(".productCase"),n=document.querySelectorAll(".productCase__name");e.length>0&&n.forEach(function(e){e.addEventListener("click",function(e){var n=this.parentElement.classList.contains("productCase_open");this.parentElement.parentElement.querySelectorAll(".productCase_open").forEach(function(e){e.classList.remove("productCase_open")}),n||this.parentElement.classList.add("productCase_open")})})}document.addEventListener("DOMContentLoaded",function(e){mainMenuOpen(),productOpen()});